import{ useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import LoginPage from "./pages/login";
import DashboardHome from "./pages/dashboard-home";
import DashboardLayout from "./pages/dashboard";
import DailyAttendance from "./pages/absensi/daily-attendance";
import SummaryAttendance from "./pages/summary/summary-attendance";
import EmployeeDetails from "./pages/employee/employee-detail";
import AdminDashboard from "./pages/admin/dashboard-admin"

const PrivateRoute = ({ isLoggedIn, children }) => {
  return isLoggedIn ? children : <Navigate to="/" replace />;
};

const PublicRoute = ({ isLoggedIn, children }) => {
  return isLoggedIn ? <Navigate to="/dashboard" replace /> : children;
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <PublicRoute isLoggedIn={isLoggedIn}>
              <LoginPage setIsLoggedIn={setIsLoggedIn} />
            </PublicRoute>
          }
        />

        <Route
          path="/dashboard"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <DashboardHome />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/absensi"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <DailyAttendance />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
         <Route
          path="/summary"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <SummaryAttendance />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/employee"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <EmployeeDetails />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="*"
          element={<Navigate to={isLoggedIn ? "/dashboard" : "/"} replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
