function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
      <Routes>
        {/* Login */}
        <Route
          path="/"
          element={
            <PublicRoute>
              <LoginPage setIsLoggedIn={setIsLoggedIn} />
            </PublicRoute>
          }
        />

        {/* Employee Routes */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute allowedRoles={["employee"]}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <DashboardHome />
              </DashboardLayout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/absensi"
          element={
            <ProtectedRoute allowedRoles={["employee"]}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <DailyAttendance />
              </DashboardLayout>
            </ProtectedRoute>
          }
        />

        {/* Admin Routes */}
        <Route
          path="/employee"
          element={
            <ProtectedRoute allowedRoles={["admin"]}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <EmployeeDetails />
              </DashboardLayout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/summary"
          element={
            <ProtectedRoute allowedRoles={["admin"]}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <SummaryAttendance />
              </DashboardLayout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin-dashboard"
          element={
            <ProtectedRoute allowedRoles={["admin"]}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <AdminDashboard />
              </DashboardLayout>
            </ProtectedRoute>
          }
        />

        {/* Fallback Routes */}
        <Route path="/unauthorized" element={<h3>Akses Ditolak</h3>} />
        <Route
          path="*"
          element={
            isLoggedIn ? (
              <Navigate
                to={
                  localStorage.getItem("role") === "admin"
                    ? "/admin-dashboard"
                    : "/dashboard"
                }
                replace
              />
            ) : (
              <Navigate to="/" replace />
            )
          }
        />
      </Routes>
    </Router>
  );
}
