import React, { useState, useEffect } from "react";
import moment from "moment";
import "moment/locale/id";
import "font-awesome/css/font-awesome.min.css";

const inputAbsen = () => {
  const [absenStatus, setAbsenStatus] = useState(0); // 0 = belum absen, 1 = masuk, 2 = pulang
  const [isWeekend, setIsWeekend] = useState(false);

  useEffect(() => {
    const today = new Date();
    const day = today.getDay(); // 0 = Minggu, 6 = Sabtu
    setIsWeekend(day === 0 || day === 6);
  }, []);

  const handleAbsenMasuk = () => {
    if (absenStatus === 0) {
      setAbsenStatus(1);
    }
  };

  const handleAbsenPulang = () => {
    if (absenStatus === 1) {
      setAbsenStatus(2);
    }
  };

  const getStatusIcon = () => {
    if (absenStatus < 2) {
      return <i className="fa fa-3x fa-exclamation-circle text-warning"></i>;
    } else {
      return <i className="fa fa-3x fa-check-circle text-success"></i>;
    }
  };

  return (
    <div className="row">
      <div className="col-12">
        <div className="card shadow-sm">
          <div className="card-header bg-primary text-white">
            <h4 className="card-title mb-0">Absen Harian</h4>
          </div>
          <div className="card-body">
            <table className="table table-bordered text-center">
              <thead className="thead-light">
                <tr>
                  <th>Status</th>
                  <th>Tanggal</th>
                  <th>Absen Masuk</th>
                  <th>Absen Pulang</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  {isWeekend ? (
                    <td colSpan="4" className="bg-light text-danger font-weight-bold">
                      Hari ini libur. Tidak perlu absen.
                    </td>
                  ) : (
                    <>
                      <td>{getStatusIcon()}</td>
                      <td>{moment().format("dddd, D MMMM YYYY")}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-primary"
                          onClick={handleAbsenMasuk}
                          disabled={absenStatus >= 1}
                          style={absenStatus >= 1 ? { cursor: "not-allowed" } : {}}
                        >
                          Absen Masuk
                        </button>
                      </td>
                      <td>
                        <button
                          className="btn btn-sm btn-success"
                          onClick={handleAbsenPulang}
                          disabled={absenStatus !== 1}
                          style={absenStatus !== 1 ? { cursor: "not-allowed" } : {}}
                        >
                          Absen Pulang
                        </button>
                      </td>
                    </>
                  )}
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default inputAbsen;
