import{ useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import LoginPage from "./pages/login";
import DashboardHome from "./pages/dashboard-home";
import DashboardLayout from "./pages/dashboard";
import DailyAttendance from "./pages/absensi/daily-attendance";
import SummaryAttendance from "./pages/summary/summary-attendance";
import EmployeeDetails from "./pages/employee/employee-detail";
import DashboardLayoutAdmin from "./pages/admin/dashboard-admin";
const PrivateRoute = ({ isLoggedIn, children }) => {
  return isLoggedIn ? children : <Navigate to="/" replace />;
};

const PublicRoute = ({ children,rolesView }) => {
  const role = localStorage.getItem("role");

  if (!rolesView.includes(role)) {
    return <Navigate to="/login" />
  }

  return children
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/dashboard/employee"
          element={
            <PublicRoute rolesView={"employee"}>
              <DashboardHome />
            </PublicRoute>
          }
        />

        <Route
          path="/dashboard/admin"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout rolesView={"admin"}>
                <DashboardLayoutAdmin />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/absensi"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <DailyAttendance />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
         <Route
          path="/summary"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <SummaryAttendance />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/employee"
          element={
            <PrivateRoute isLoggedIn={isLoggedIn}>
              <DashboardLayout setIsLoggedIn={setIsLoggedIn}>
                <EmployeeDetails />
              </DashboardLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="*"
          element={<Navigate to={isLoggedIn ? "/dashboard" : "/"} replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
