import React, { useState, useEffect } from "react";
import moment from "moment";
import "moment/locale/id";
import "font-awesome/css/font-awesome.min.css";

const DailyAttendance = () => {
  const [absenStatus, setAbsenStatus] = useState(0); // 0: belum absen, 1: masuk, 2: pulang
  const [isWeekend, setIsWeekend] = useState(false);

  useEffect(() => {
    const today = new Date();
    const day = today.getDay(); // 0 = Minggu, 6 = Sabtu
    setIsWeekend(day === 0 || day === 6);
  }, []);

  const handleAbsenMasuk = () => {
    if (absenStatus === 0) {
      setAbsenStatus(1);
    }
  };

  const handleAbsenPulang = () => {
    if (absenStatus === 1) {
      setAbsenStatus(2);
    }
  };

  const getStatusInfo = () => {
    if (absenStatus === 0) {
      return { icon: "exclamation-circle", text: "Belum Absen", color: "warning" };
    }
    if (absenStatus === 1) {
      return { icon: "sign-in", text: "Sudah Absen Masuk", color: "info" };
    }
    return { icon: "check-circle", text: "Absen Lengkap", color: "success" };
  };

  const status = getStatusInfo();

  return (
    <div className="container py-4">
      <div className="row justify-content-center">
        <div className="col-lg-10">
          <div className="card shadow-sm border-0">
            <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">Absen Harian</h5>
              <span className="badge bg-light text-dark">{moment().format("dddd, D MMMM YYYY")}</span>
            </div>

            <div className="card-body">
              {isWeekend ? (
                <div className="alert alert-warning text-center fw-semibold">
                  <i className="fa fa-info-circle me-2"></i>
                  Hari ini adalah hari libur. Tidak perlu melakukan absensi.
                </div>
              ) : (
                <div className="text-center">
                  <div className="mb-4">
                    <i className={`fa fa-3x fa-${status.icon} text-${status.color}`}></i>
                    <p className="mt-2 fw-semibold text-${status.color}">{status.text}</p>
                  </div>

                  <div className="d-flex justify-content-center gap-3">
                    <button
                      className="btn btn-outline-primary px-4"
                      onClick={handleAbsenMasuk}
                      disabled={absenStatus >= 1}
                    >
                      <i className="fa fa-sign-in me-2"></i>Absen Masuk
                    </button>
                    <button
                      className="btn btn-outline-success px-4"
                      onClick={handleAbsenPulang}
                      disabled={absenStatus !== 1}
                    >
                      <i className="fa fa-sign-out me-2"></i>Absen Pulang
                    </button>
                  </div>
                </div>
              )}
            </div>

            {!isWeekend && (
              <div className="card-footer text-muted text-end">
                Status:{" "}
                <span className={`badge bg-${status.color}`}>
                  {status.text}
                </span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DailyAttendance;
